#include "posix.h"

#include <limits.h>
#include <string.h>

#include "filesystem.h"
#include "log.h"
#include "output.h"
#include "workspace.h"

static void
write_hdr(FILE *out, struct workspace *wk)
{
	fprintf(
		out,
		"# This is the build file for project \"%s\"\n"
		"# It is autogenerated by the boson build system.\n"
		"\n"
		"ninja_required_version = 1.7.1\n"
		"\n"
		"# Rules for compiling.\n"
		"\n"
		"rule c_COMPILER\n"
		" command = cc $ARGS -MD -MQ $out -MF $DEPFILE -o $out -c $in\n"
		" deps = gcc\n"
		" depfile = $DEPFILE_UNQUOTED\n"
		" description = Compiling C object $out\n"
		"\n"
		"# Rules for linking.\n"
		"\n"
		"rule c_LINKER\n"
		" command = cc $ARGS -o $out $in $LINK_ARGS\n"
		" description = Linking target $out\n"
		"\n"
		"# Other rules\n"
		"\n"
		"rule CUSTOM_COMMAND\n"
		" command = $COMMAND\n"
		" description = $DESC\n"
		" restat = 1\n"
		"\n"
		"# Phony build target, always out of date\n"
		"\n"
		"build PHONY: phony \n"
		"\n"
		"# Build rules for targets\n",
		wk->project.name
		);
}

struct write_tgt_iter_ctx {
	struct obj *tgt;
	FILE *out;
	const char *args;
	uint32_t args_id;
};

static char *
path_without_slashes(const char *path)
{
	uint32_t i;
	static char buf[PATH_MAX + 1];

	strncpy(buf, path, PATH_MAX);
	for (i = 0; buf[i]; ++i) {
		if (buf[i] == '/') {
			buf[i] = '_';
		}
	}
	return buf;

}

enum iteration_result
write_tgt_iter(struct workspace *wk, void *_ctx, uint32_t val_id)
{
	struct write_tgt_iter_ctx *ctx = _ctx;
	struct obj *src = get_obj(wk, val_id);
	assert(src->type == obj_file); // TODO
	const char *path = wk_str(wk, src->dat.file), *pws = path_without_slashes(path);

	fprintf(ctx->out,
		"build %s.p/%s.o: c_COMPILER %s\n"
		" DEPFILE = %s.p/%s.o.d\n"
		" DEPFILE_UNQUOTED = %s.p/%s.o.d\n"
		" ARGS = %s\n\n",
		ctx->tgt->dat.tgt.name,
		pws,
		path,
		ctx->tgt->dat.tgt.name,
		pws,
		ctx->tgt->dat.tgt.name,
		pws,
		ctx->args
		);

	return ir_cont;
}

enum iteration_result
write_tgt_obj_name_iter(struct workspace *wk, void *_ctx, uint32_t val_id)
{
	struct write_tgt_iter_ctx *ctx = _ctx;
	const char *path = wk_str(wk, get_obj(wk, val_id)->dat.file),
		   *pws = path_without_slashes(path);

	fprintf(ctx->out, "%s.p/%s.o ", ctx->tgt->dat.tgt.name, pws);

	return ir_cont;
}

enum iteration_result
make_args_iter(struct workspace *wk, void *_ctx, uint32_t val_id)
{
	struct write_tgt_iter_ctx *ctx = _ctx;
	struct obj *arg = get_obj(wk, val_id);
	assert(arg->type == obj_string); // TODO

	wk_strapp(wk, &ctx->args_id, "%s ", arg->dat.s);

	return ir_cont;
}

static bool
write_tgt(FILE *out, struct workspace *wk, uint32_t tgt_id)
{
	struct obj *tgt = get_obj(wk, tgt_id);
	L(log_out, "writing rules for target '%s'", tgt->dat.tgt.name);

	struct write_tgt_iter_ctx ctx = { .tgt = tgt, .out = out };

	ctx.args_id = wk_str_pushf(wk, "-I%s.p -I. -I.. ", tgt->dat.tgt.name);

	if (tgt->dat.tgt.include_directories) {
		struct obj *inc = get_obj(wk, tgt->dat.tgt.include_directories);
		assert(inc->type == obj_file); // TODO

		wk_strapp(wk, &ctx.args_id, "-I%s ", wk_str(wk, inc->dat.file));
	}

	if (!obj_array_foreach(wk, wk->project.args, &ctx, make_args_iter)) {
		return false;
	}

	ctx.args = wk_str(wk, ctx.args_id);

	if (!obj_array_foreach(wk, tgt->dat.tgt.src, &ctx, write_tgt_iter)) {
		return false;
	}

	fprintf(out, "build %s: c_LINKER ", tgt->dat.tgt.name);

	if (!obj_array_foreach(wk, tgt->dat.tgt.src, &ctx, write_tgt_obj_name_iter)) {
		return false;
	}

	fprintf(out, "\n LINK_ARGS = -Wl,--as-needed -Wl,--no-undefined\n");

	return true;
}

static FILE *
setup_outdir(const char *dir)
{
	if (!fs_dir_exists(dir)) {
		if (!fs_mkdir(dir)) {
			return false;
		}
	}

	char path[PATH_MAX + 1] = { 0 };
	snprintf(path, PATH_MAX, "%s/%s", dir, "build.ninja");

	FILE *out;
	if (!(out = fs_fopen(path, "w"))) {
		return NULL;
	}

	return out;
}

bool
output_build(struct workspace *wk, const char *dir)
{
	FILE *out;
	if (!(out = setup_outdir(dir))) {
		return false;
	}

	write_hdr(out, wk);

	uint32_t i;
	for (i = 0; i < wk->tgts.len; ++i) {
		if (!write_tgt(out, wk, *(uint32_t *)darr_get(&wk->tgts, i))) {
			return false;
		}
	}

	if (!fs_fclose(out)) {
		return false;
	}

	return true;
}
